/*
 * Single
 *
 * Comment
 *
*/
.comments-wrapper{float:left;width:100%;position:relative;margin-top:10px;z-index:0}
.rtl .comments-wrapper{float:right;}
.video-fullwidth .comments-wrapper{width:100%;margin:0 auto;float:none;}
.comments {overflow:hidden;width:100%;display:inline-block}
.comments h2{display:inline-block;margin-bottom:10px;width:100%}
.comment-closed{text-align:center;width:96%}
.comment-closed p{padding:2% 2% 1%}
.comments-modal {width:100%;margin:5px 0;padding:5px 0 10px;text-align:center;border-bottom: 1px solid #eee;}
.dark .comments-modal {border-bottom: 1px solid #292929;}
.com-btn {width:100%;border-radius:2px;}
/*
 * Single
 *
 * Comment style
 *
*/
.comment{clear:both;display:block;margin:0 0 20px;position:relative}
/*
 * Comment Avatar
 *
*/
.gravatar{width:50px;height:50px;margin-right:15px;float:left;}
.gravatar img{-moz-border-radius:50%;-webkit-border-radius:50%;border-radius:50%}
.depth-2 .gravatar,
.depth-2 .gravatar img{width:35px;height:35px;}
.rtl .gravatar{margin-right:0;margin-left:20px;float:right}
/*
 * Comment body
 *
*/
.comment-head{display:inline-block;width:60%;margin:0 0 5px;}
.comment-head h3{display:inline-block;width:auto;color:#31313c}
.comment-head h3 a{color:#31313c;}
.dark .comment-head h3 a{color:#fff;}
.comment-head span{display:inline-block;color:#656c7a;padding:0 5px}
.dark .comment-name, 
.dark .comment-email,
.dark .comment-name input, 
.dark .comment-email input,
.dark .comment-form textarea{background-color:#282828;color:#ccc;border:1px solid #444 !important;}
.dark .comment-head h3 {color:#fff}
.dark .comment-head span {color:#aaa}
/*
 * Comment body
 *
*/
.comment-moderation {color:red}
.comment-content{display:table;margin:5px 0 0 65px;padding:0 0 5px}
.comment-content p{display:inline-block;padding:0 0 5px}
.rtl .comment-content{margin:5px 65px 0 0}
.dark .comment-moderation {color:#fff;border-radius:3px;padding:5px;border:1px solid #e74c3c}
/*
 * Comment Footer
 *
*/
.comment-footer{color:#656c7a;display:inline-block;margin:0 0 10px 65px !important}
.dark .comment-footer{color:#aaa}
.comment-footer * {display:inline-block;line-height:1.3;vertical-align:middle}
.comment-footer a{color:#656c7a;line-height:1.3;padding:0 5px 0 0;vertical-align:middle}
.dark .comment-footer a{color:#aaa}
.dark .comment-footer a:hover{color:#fff}
.comment-footer {width:100%;}
.comment-footer .comment-reply-link,
.comment-footer .comment-edit-link{text-transform:uppercase;vertical-align:top}
.rtl .comment-footer a{padding:0 0 0 5px}
.rtl .comment-footer{margin:0 65px 10px 0 !important}
.rtl .comment-footer .comment-reply-link{vertical-align:super}
.comment-footer .comment-reply-link{font-weight:700!important;}
/*
 * Comment like & dislike
 *
*/
.comment-ld .cb-like,
.comment-ld .cb-dislike{cursor:pointer;}
.comment-ld .cb-like .icon svg{top:-1px;position:relative;}
.comment-ld .cb-like .icon svg,
.comment-ld .cb-dislike .icon svg{fill:#656c7a;height:16px;width:16px;}
.dark .comment-ld .cb-like .icon svg,
.dark .comment-ld .cb-dislike .icon svg{fill:#909090;}
.comment-ld .cb-like .like-count,
.comment-ld .cb-dislike .dislike-count{margin:0 7px}
.comment-ld .u-disliked svg,
.comment-ld .u-liked,
.dark .comment-ld .u-disliked svg,
.dark .comment-ld .u-liked svg{fill:#428bca!important}
/*
 * Comment Depth
 *
*/
.children{margin:20px 0 0}
.depth-2{margin:0 0 25px 65px}
.depth-2 .comment-footer {margin:0 0 0 50px}
.depth-2 .comment-content{margin:5px 0 0 50px}
.rtl .depth-2{margin:0 65px 25px 0}
.rtl .depth-2 .comment-content{margin:5px 50px 0 0}
#thread{display:inline-block;margin:20px 0 0;width:100%}
#thread .comment-respond{margin:0 auto;width:80%}
/*
 * Single
 *
 * Comments Form 
 *
*/
.comment-form{float:left;margin:10px 0;width:100%}
.comment-click {display:none}
.comment-row{display:inline-block;width:100%;margin:0 0 10px}
.comment-name,
.comment-email{display:inline-block;width:49%;line-height:1.5;border:1px solid #ddd;border-radius:3px}
.comment-name{margin:0 2% 0 0}
.rtl .comment-name{margin:0 0 0 2%}

.modal-body .comment-name{margin:0 1% 0 0}
.rtl .modal-body .comment-name{margin:0 0 0 1%}

.comment-email{margin:0}
.comment-name input,
.comment-email input{width:100%;padding:8px 10px}
.comment-textarea{display:inline-block;width:100%;margin-bottom:10px}
.comment-textarea textarea{width:100%;border:1px solid #ddd;padding:2%;border-radius:3px}
#cancel-comment-reply-link {margin:0 5px 0 0;}
.rtl #cancel-comment-reply-link {margin:0 0 0 5px}
.required-error{border:1px solid #eb8787!important}
.dark .required-error{border:1px solid #e74c3c!important}
.rtl .comment-form{float:right}
/*
 * Single
 *
 * Notify of new replies to this comment
 *
*/
.comment-checkbox {margin:0 5px}
.comment-checkbox input[type="checkbox"],
.comment-checkbox .checked {display:none}
.comment-checkbox input[type="checkbox"]:checked ~ .checked{display:inline-block}
.comment-checkbox input[type="checkbox"]:checked ~ .unchecked{display:none}
.comment-checkbox i.unchecked{color:#999}
.comment-checkbox svg{height:20px;width:20px;display:block;fill:#fff;}
.comment-checkbox .unchecked svg{fill:#777;}
/*
 * Single
 *
 * Alerts
 *
*/
.comments-alerts,
.comments-rules,
.comments-note,
.comments-rules strong{float:left;margin:0 0 10px;width:100%}
.comments-alerts{margin:0}
.comment-closed-note{margin-top:15px}
.comment-must-login{margin-top:10px}
.rtl .comment-form,
.rtl .comments-alerts,
.rtl .comments-rules,
.rtl .comments-note,
.rtl .comments-rules strong{float:right}
/*
 * Single
 *
 * Comments Form 
 *
 * ------> Rules
*/
.comments-rules strong{margin:0}
/*
 * Single
 *
 * Comments Pager
 *
*/
.comments-nav{float:left;width:100%;margin:15px 0;text-align:center}
.comments-nav .page-numbers{border:2px solid #eee;color:#5d6769;margin-bottom:5px;margin-right:5px;padding:8px 16px;width:auto!important;display:inline-block}
.comments-nav .next,
.comments-nav .prev{display:none}
.comments-nav .current{}
.rtl .comments-nav{float:right}
.rtl .comments-nav .page-numbers{margin-right:0;margin-left:5px}
/*
 * Single
 *
 * Disqus Comments 
 *
*/
.disqus-wrap{float:left;margin:0 0 10px;width:100%}
.rtl .disqus-wrap{float:right}
/*
 * Single
 *
 * Facebook Comments Box
 *
*/
.fb-comments-wrap{float:left;width:100%;margin:10px 0 0}
.fb-comments-wrap,
.fb-comments,
.fb-comments iframe[style],
.fb-comments span,
.fb-comments iframe span[style]{width:100%!important}
.facebook-comments{background-color:#ebedef;float:left;min-height:120px;padding:1%;width:98%}
.rtl .fb-comments-wrap,
.rtl .facebook-comments{float:right}
